mapper.update:
UPDATE project_board SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no, name, subject, content, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI:SS') as dbday, hit 
FROM project_board 
WHERE no=#{no}

mapper.update:
UPDATE project_board SET 
name=#{name}, subject=#{subject}, content=#{content}
WHERE no=#{no}
